// DropDownMenu.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './DropDownMenu.css'; // Import CSS file for styling

function DropdownMenu() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="dropdown">
      <button className="dropdown-toggle" onClick={toggleMenu}>
        Open Dropdown
      </button>
      {isOpen && (
        <div className="dropdown-menu">
          <Link to="/paybill/create-pin" className="dropdown-item">Create Pin</Link>
          <Link to="/paybill/source1" className="dropdown-item">Source 1</Link>
          <Link to="/paybill/source2" className="dropdown-item">Source 2</Link>
        </div>
      )}
    </div>
  );
}

export default DropdownMenu;
