import React from "react";
import { NavLink } from "react-router-dom";
import WalletIcon from "../../assets/images/wallet.png"; // Make sure to import the Wallet icon

function PayBillDropDown({ showDropdown, closeMobileMenu }){
  return (
    <li className={`nav-item ${showDropdown ? "dropdown show" : "dropdown"}`}>
      <div className="text-link dropdown-toggle" onClick={closeMobileMenu}>

        Pay Bill
      </div>
      {showDropdown && (
        <div className="dropdown-menu show">
          <NavLink
            to="/PayBill/source1"
            className="dropdown-item source1"
            onClick={closeMobileMenu}
          >
            Source 1
          </NavLink>
          <NavLink
            to="/PayBill/source2"
            className="dropdown-item source2"
            onClick={closeMobileMenu}
          >
            Source 2
          </NavLink>
          <NavLink
            to="/PayBill/create-pin"
            className="dropdown-item create-pin"
            onClick={closeMobileMenu}
          >
            Create Pin
          </NavLink>
        </div>
      )}
    </li>
  );
};

export default PayBillDropDown;
